function isValidPassword(str) {
  if(str.length20) {
    return false;
  }
  var num = 0;
  if(/[A-Z]/.test(str)) {
    num++;
  }
  if(/[a-z]/.test(str)) {
    num++;
  }
  if(/[0-9]/.test(str)) {
    num++;
  }
  if(/_/.test(str)) {
    num++;
  }
  if(num >= 2) {
    return true;
  } else {
    return false;
  }
}
